import React from 'react';
import './styles.css';

const Card = (props)=> {
    const { hero } = props;
    return(
        <div>
            <img src={`${hero.thumbnail.path}.${hero.thumbnail.extension}`} alt="imagem" className="imagem"/>
            <p className="nome">{hero.name}</p>
        </div>
    );
}

export default Card;
